mapper.select:
SELECT
-- 级联表的字段 ,sucai.yonghu_id as sucaiYonghuId ,sucai.sucai_name as sucaiName ,sucai.sucai_photo as sucaiPhoto ,sucai.sucai_types as sucaiTypes ,sucai.sucai_file as sucaiFile ,sucai.sucai_clicknum as sucaiClicknum ,sucai.shangxia_types as shangxiaTypes ,sucai.sucai_content as sucaiContent ,sucai.sucai_delete as sucaiDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_email as yonghuEmail
FROM sucai_collection a left JOIN sucai sucai ON a.sucai_id = sucai.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.sucai_id = #{params.sucaiId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.sucai_collection_types = #{params.sucaiCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.sucai_id IS NOT NULL
and sucai.sucai_name like CONCAT('%',#{params.sucaiName},'%')
and sucai.sucai_types = #{params.sucaiTypes}
and sucai.sucai_clicknum >= #{params.sucaiClicknumStart}
and sucai.sucai_clicknum <= #{params.sucaiClicknumEnd}
and sucai.sucai_clicknum = #{params.sucaiClicknum}
and sucai.shangxia_types = #{params.shangxiaTypes}
and sucai.sucai_content like CONCAT('%',#{params.sucaiContent},'%')
and sucai.sucai_delete >= #{params.sucaiDeleteStart}
and sucai.sucai_delete <= #{params.sucaiDeleteEnd}
and sucai.sucai_delete = #{params.sucaiDelete}
and UNIX_TIMESTAMP(sucai.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(sucai.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
